import React from "react"
import memesData from "../memesData.js"

export default function Meme() {
    const [meme, setMeme] = React.useState({
        topText: "",
        bottomText: "",
        randomImage: "http://i.imgflip.com/1bij.jpg" 
    })
    // eslint-disable-next-line no-unused-vars
    const [allMemeImages, setAllMemeImages] = React.useState(memesData)



    function getMemeImage() {
        const memesArray = allMemeImages.data.memes
        const randomNumber = Math.floor(Math.random() * memesArray.length)
        const url = memesArray[randomNumber].url
        setMeme(prevMeme => ({
            ...prevMeme,
            randomImage: url
        }))


    }
    return (
        <main>
            <div>
                <input type="text" required="" placeholder="Shut up!" className="int"></input>
                <input type="text" required="" placeholder="Take my money" className="int--2"></input>
                <button className="btn" onClick={getMemeImage}>Get new Image</button>
            </div>
            <img src={meme.randomImage} className="meme" alt="meme--" />
        </main>
    )
}
